var TemplateEngine;
var jade = require('jade');

TemplateEngine = function() {
  return this;
};

TemplateEngine.prototype.default = function(expressInstance, baseRoot, cache) {
  cache = cache ? cache : false
  expressInstance.engine('jade', jade.__express);
  expressInstance.set('view options', {
    basedir: baseRoot
  });
  expressInstance.set('view cache', cache);
  expressInstance.set('views', baseRoot);
  expressInstance.locals.basedir = baseRoot;
  expressInstance.set('view engine', 'jade');
  return this;
};

module.exports = new TemplateEngine;
